record.field:
if (record.date) and (record.employee_id.id):
  iset_ts = env['iset.timesheet'].search([('date','=',record.date),('employee_id','=',record.employee_id.id)])
  if not iset_ts.id:
    name = record.employee_id.name + " - " + str(record.date)
    iset_ts = env['iset.timesheet'].create({'name':name, 'date':record.date, 'employee_id':record.employee_id.id})
  record['iset_timesheet_id'] = iset_ts.id

